package com.lcvc.ebuy_hcs.web.backstage;import com.lcvc.ebuy_hcs.dao.AdminDao;import com.lcvc.ebuy_hcs.model.Admin;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpSession;@Controllerpublic class LoginController {	AdminDao admindao=new AdminDao();	@RequestMapping(value = "/backstage/login", method = RequestMethod.POST)	public String hello(String username, String password, HttpSession session) {		Admin admin=admindao.login(username, password);		if(admin!=null/*admindao.login(username, password*/){			/*session.setAttribute("name",admindao.name(username));*/			session.setAttribute("Admin",admin/*admindao.getAdmin(username*/);			return ("redirect:/backstage/index");		}else {			return ("/jsp/backstage/login.jsp");		}	}	@RequestMapping(value = "/backstage/index")	public String hello() {			return ("/jsp/backstage/main.jsp");	}}