package com.lcvc.ebuy_hcs.web.backstage;import com.lcvc.ebuy_hcs.dao.AdminDao;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import javax.servlet.http.HttpServletRequest;@Controllerpublic class LoginController {	AdminDao admindao=new AdminDao();	@RequestMapping(value = "/backstage/login", method = RequestMethod.POST)	public String hello(String username,String password) {		if(admindao.login(username, password)){			return ("jsp/backstage/main.jsp");		}else {			return ("/jsp/backstage/login.jsp");		}	}}